mapper.update:
update str_store set dsr_id=#{dsr_id}, plataeform_type=#{plataeform_type}, store_name=#{store_name}, store_sts_cd=#{store_sts_cd}, created_by=#{created_by}, creation_date=#{creation_date},
last_update_by=#{last_update_by}, last_update_date=#{last_update_date}, call_cnt=#{call_cnt}, remark=#{remark},
    sts_cd=#{sts_cd} where str_id=#{str_id}

mapper.select:
select * from str_store

    str_id = #{str_id}
    dsr_id = #{dsr_id}
    plataeform_type = #{plataeform_type}
    store_name = #{store_name}
    store_sts_cd=#{store_sts_cd}
    created_by = #{created_by}
    creation_date = #{creation_date}
    last_update_by = #{last_update_by}
    last_update_date = #{last_update_date}
    call_cnt = #{call_cnt}
    remark = #{remark}
    sts_cd = #{sts_cd}
