mapper.update:
update pro_product set sku_cd=#{sku_cd}, brd_id=#{brd_id}, man_id=#{man_id},
    title=#{title}, upc=#{upc}, ean=#{ean}, model=#{model}, warranty_day=#{warranty_day},
    replenishment_period=#{replenishment_period}, warranty=#{warranty}, time_unit=#{time_unit}
     where pro_id=#{pro_id}

mapper.select:
select * from pro_product

    pro_id = #{pro_id}
    sku_cd = #{sku_cd}
    brd_id = #{brd_id}
    man_id = #{man_id}
    title = #{title}
    upc = #{upc}
    ean = #{ean}
    model = #{model}
    warranty_day = #{warranty_day}
    replenishment_period = #{replenishment_period}
    warranty = #{warranty}
    time_unit = #{time_unit}
